package 데이터;

public class 기본데이터확인문제2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final double PI = 3.14;  //final 변경불가>>알아보기쉽게 대문자, 상수
		double r = 2.2;
		
		//추상적 > 명확하지않은, 아직은 불완전 abstract
		//구현 implement > 명확하고 완전하게 만드는 것
		System.out.println("원의 면적은: " + PI * r * r);
	}

}
